"use strict";(self.webpackChunkwoodpecker=self.webpackChunkwoodpecker||[]).push([[7332],{96196:t=>{t.exports=JSON.parse('{"name":"Mastodon Post","url":"https://codeberg.org/woodpecker-plugins/mastodon-post","icon":"https://joinmastodon.org/logos/logo-purple.svg","description":"Post statuses to Mastodon.","docs":"<h1>Mastodon Post plugin</h1>\\n<p>Woodpecker plugin to post statuses to Mastodon.</p>\\n<h2>Usage</h2>\\n<pre><code class=\\"language-yaml\\">image: woodpeckerci/plugin-mastodon-post\\nsettings:\\n  server: https://mastodon.social\\n  access_token: your-access-token\\n  status: We just released {{ CI_COMMIT_TAG }} \ud83d\ude80\\n</code></pre>\\n<h3>Settings</h3>\\n<table>\\n<thead>\\n<tr>\\n<th>Setting</th>\\n<th>Description</th>\\n</tr>\\n</thead>\\n<tbody><tr>\\n<td><code>server_url</code></td>\\n<td>Mastodon server url, including protocol. <strong>Required</strong></td>\\n</tr>\\n<tr>\\n<td><code>access_token</code></td>\\n<td>Mastodon access token. <strong>Required</strong></td>\\n</tr>\\n<tr>\\n<td><code>status</code></td>\\n<td>Status message (You can use variables like <code>{{ CI_COMMIT_TAG }}</code>) <strong>Required</strong></td>\\n</tr>\\n<tr>\\n<td><code>sensitive</code></td>\\n<td>Mark post as sensitive (<code>true</code>)</td>\\n</tr>\\n<tr>\\n<td><code>media</code></td>\\n<td><code>,</code>-separated list of paths to attachments</td>\\n</tr>\\n<tr>\\n<td><code>in_reply_to_id</code></td>\\n<td>Post as reply to this post (ID)</td>\\n</tr>\\n<tr>\\n<td><code>spoiler_text</code></td>\\n<td>Spoiler text</td>\\n</tr>\\n<tr>\\n<td><code>visibility</code></td>\\n<td>Visibility (<code>private</code>, <code>unlisted</code>, <code>public</code> or <code>direct</code>)</td>\\n</tr>\\n<tr>\\n<td><code>language</code></td>\\n<td>Status language</td>\\n</tr>\\n</tbody></table>\\n","tags":["post","status","mastodon"],"containerImage":"woodpeckerci/plugin-mastodon-post","containerImageUrl":"https://hub.docker.com/r/woodpeckerci/plugin-mastodon-post","verified":true}')}}]);